
# Table structure for table `config`
#

CREATE TABLE `config` (
  `id` mediumint(128) unsigned NOT NULL AUTO_INCREMENT,
  `form` enum('postfix-main.cf', 'email-api-php') DEFAULT 'postfix-main.cf'
  `name` varchar(255) NOT NULL DEFAULT '',
  `description` varchar(255) NOT NULL DEFAULT '',
  `values` tinytext,
  `default` tinytext,
  `fingerprint` varchar(32),
  `typal` enum('string', 'integer', 'array', 'yesno', 'timeunits', 'secondunits', 'mysql-script', 'email', 'emails', 'path', 'file', 'other') DEFAULT 'other'
  `weight` varchar(255) NOT NULL DEFAULT '',
  `created` int(11) NOT NULL DEFAULT '0',
  `changed` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `nameweightcreated` (`name`,`weight`,`created`)
) ENGINE=INNODB DEFAULT CHARSET=utf8;

