INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'lmtp_bind_address', 'The LMTP-specific version of the smtp_bind_address configuration parameter. See there for details.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'lmtp_bind_address6', 'The LMTP-specific version of the smtp_bind_address6 configuration parameter. See there for details.',  '', 'string', '1', UNIX_TIMESTAMP());
