INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'lmtp_cache_connection', 'Keep Postfix LMTP client connections open for up to $max_idle seconds. When the LMTP client receives a request for the same connection the connection is reused.',  'yes', 'yesno', '1', UNIX_TIMESTAMP());
