INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'lmtp_destination_concurrency_limit', 'The maximal number of parallel deliveries to the same destination via the lmtp message delivery transport. This limit is enforced by the queue manager. The message delivery transport name is the first field in the entry in the master.cf file.',  '$default_destination_concurrency_limit', 'string', '1', UNIX_TIMESTAMP());
