INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'local_command_shell', 'Optional shell program for local(8) delivery to non-Postfix command. By default, non-Postfix commands are executed directly; commands are given to given to the default shell (typically, /bin/sh) only when they contain shell meta characters or shell built-in commands.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'local_delivery_status_filter', 'Optional filter for the local(8) delivery agent to change the status code or explanatory text of successful or unsuccessful deliveries. See default_delivery_status_filter for details.',  '$default_delivery_status_filter', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'local_destination_concurrency_limit', 'The maximal number of parallel deliveries via the local mail delivery transport to the same recipient (when "local_destination_recipient_limit = 1") or the maximal number of parallel deliveries to the same local domain (when "local_destination_recipient_limit > 1"). This limit is enforced by the queue manager. The message delivery transport name is the first field in the entry in the master.cf file.',  '2', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'local_destination_recipient_limit', 'The maximal number of recipients per message delivery via the local mail delivery transport. This limit is enforced by the queue manager. The message delivery transport name is the first field in the entry in the master.cf file.',  '1', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'local_header_rewrite_clients', 'Rewrite message header addresses in mail from these clients and update incomplete addresses with the domain name in $myorigin or $mydomain; either don't rewrite message headers from other clients at all, or rewrite message headers and update incomplete addresses with the domain specified in the remote_header_rewrite_domain parameter.',  'permit_inet_interfaces', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'local_recipient_maps', 'Lookup tables with all names or addresses of local recipients: a recipient address is local when its domain matches $mydestination, $inet_interfaces or $proxy_interfaces. Specify @domain as a wild-card for domains that do not have a valid recipient list. Technically, tables listed with $local_recipient_maps are used as lists: Postfix needs to know only if a lookup string is found or not, but it does not use the result from table lookup.',  'proxy:unix:passwd.byname $alias_maps', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'local_transport', 'The default mail delivery transport and next-hop destination for final delivery to domains listed with mydestination, and for [ipaddress] destinations that match $inet_interfaces or $proxy_interfaces. This information can be overruled with the transport(5) table.',  'local:$myhostname', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'luser_relay', 'Optional catch-all destination for unknown local(8) recipients. By default, mail for unknown recipients in domains that match $mydestination, $inet_interfaces or $proxy_interfaces is returned as undeliverable.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mail_name', 'The mail system name that is displayed in Received: headers, in the SMTP greeting banner, and in bounced mail.',  'Postfix', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mail_owner', 'The UNIX system account that owns the Postfix queue and most Postfix daemon processes. Specify the name of an unprivileged user account that does not share a user or group ID with other accounts, and that owns no other files or processes on the system. In particular, don't specify nobody or daemon. PLEASE USE A DEDICATED USER ID AND GROUP ID',  'postfix', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mail_release_date', 'The Postfix release date, in "YYYYMMDD" format.',  '20200101', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mail_spool_directory', 'The directory where local(8) UNIX-style mailboxes are kept. The default setting depends on the system type. Specify a name ending in / for maildir-style delivery.',  '/var/mail', 'path', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mail_version', 'The version of the mail system. Stable releases are named major.minor.patchlevel. Experimental releases also include the release date. The version string can be used in, for example, the SMTP greeting banner.',  '3.3.0', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mailbox_command', 'Optional external command that the local(8) delivery agent should use for mailbox delivery. The command is run with the user ID and the primary group ID privileges of the recipient. Exception: command delivery for root executes with $default_privs privileges. This is not a problem, because 1) mail for root should always be aliased to a real user and 2) don't log in as root, use "su" instead.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mailbox_command_maps', 'Optional lookup tables with per-recipient external commands to use for local(8) mailbox delivery. Behavior is as with mailbox_command.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mailbox_delivery_lock', 'How to lock a UNIX-style local(8) mailbox before attempting delivery.',  'fcntl, dotlock', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mailbox_size_limit', 'The maximal size of any local(8) individual mailbox or maildir file, or zero (no limit). In fact, this limits the size of any file that is written to upon local delivery, including files written by external commands that are executed by the local(8) delivery agent.',  '51200000', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mailbox_transport', 'Optional message delivery transport that the local(8) delivery agent should use for mailbox delivery to all local recipients, whether or not they are found in the UNIX passwd database.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mailbox_transport_maps', 'Optional lookup tables with per-recipient message delivery transports to use for local(8) mailbox delivery, whether or not the recipients are found in the UNIX passwd database.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'maillog_file', 'The name of an optional logfile that is written by the Postfix postlogd(8) service. An empty value selects logging to syslogd(8). Specify "/dev/stdout" to select logging to standard output. Stdout logging requires that Postfix is started with "postfix start-fg"',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'maillog_file_compressor', 'The program to run after rotating $maillog_file with "postfix logrotate". The command is run with the rotated logfile name as its first argument.',  'gzip', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'maillog_file_prefixes', 'A list of allowed prefixes for a maillog_file value. This is a safety feature to contain the damage from a single configuration mistake. Specify one or more prefix strings, separated by comma or whitespace.',  '/var, /dev/stdout', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'maillog_file_rotate_suffix', 'The format of the suffix to append to $maillog_file while rotating the file with "postfix logrotate". See strftime(3) for syntax. The default suffix, YYYYMMDD-HHMMSS, allows logs to be rotated frequently.',  '%Y%M%d-%H%M%S', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mailq_path', 'Sendmail compatibility feature that specifies where the Postfix mailq(1) command is installed.',  '/usr/bin/mailq', 'file', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'manpage_directory', 'Where the Postfix manual pages are installed.',  '/usr/share/man', 'path', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'maps_rbl_reject_code', 'The numerical Postfix SMTP server response code when a remote SMTP client request is blocked by the reject_rbl_client, reject_rhsbl_client, reject_rhsbl_reverse_client, reject_rhsbl_sender or reject_rhsbl_recipient restriction.',  '554', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'masquerade_classes', 'What addresses are subject to address masquerading.',  'envelope_sender, header_sender, header_recipient', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'masquerade_domains', 'Optional list of domains whose subdomain structure will be stripped off in email addresses.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'masquerade_exceptions', 'Optional list of user names that are not subjected to address masquerading, even when their addresses match $masquerade_domains.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'master_service_disable', 'Selectively disable master(8) listener ports by service type or by service name and type. Specify a list of service types ("inet", "unix", "fifo", or "pass") or "name/type" tuples, where "name" is the first field of a master.cf entry and "type" is a service type. As with other Postfix matchlists, a search stops at the first match. Specify "!pattern" to exclude a service from the list. By default, all master(8) listener ports are enabled.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'max_idle', 'The maximum amount of time that an idle Postfix daemon process waits for an incoming connection before terminating voluntarily. This parameter is ignored by the Postfix queue manager and by other long-lived Postfix daemon processes.',  '1m 40s', 'timeunits', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'max_use', 'The maximal number of incoming connections that a Postfix daemon process will service before terminating voluntarily. This parameter is ignored by the Postfix queue manager and by other long-lived Postfix daemon processes.',  '100', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'maximal_backoff_time', 'The maximal time between attempts to deliver a deferred message.',  '4000s', 'timeunits', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'maximal_queue_lifetime', 'Consider a message as undeliverable, when delivery fails with a temporary error, and the time in the queue has reached the maximal_queue_lifetime limit.',  '5d', 'timeunits', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'message_drop_headers', 'Names of message headers that the cleanup(8) daemon will remove after applying header_checks(5) and before invoking Milter applications.',  'bcc, content-length, resent-bcc, return-path', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'message_reject_characters', 'The set of characters that Postfix will reject in message content. The usual C-like escape sequences are recognized: \a \b \f \n \r \t \v \ddd (up to three octal digits) and \\.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'message_size_limit', 'The maximal size in bytes of a message, including envelope information.',  '10240000', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'message_strip_characters', 'The set of characters that Postfix will remove from message content. The usual C-like escape sequences are recognized: \a \b \f \n \r \t \v \ddd (up to three octal digits) and \\.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'meta_directory', 'The location of non-executable files that are shared among multiple Postfix instances, such as postfix-files, dynamicmaps.cf, and the multi-instance template files main.cf.proto and master.cf.proto. This directory should contain only Postfix-related files. Typically, the meta_directory parameter has the same default as the config_directory parameter (/etc/postfix or /usr/local/etc/postfix).',  '/etc/postfix', 'path', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_command_timeout', 'The time limit for sending an SMTP command to a Milter (mail filter) application, and for receiving the response.',  '30s', 'timeunits', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_connect_macros', 'The macros that are sent to Milter (mail filter) applications after completion of an SMTP connection. See MILTER_README for a list of available macro names and their meanings.',  'j {daemon_name} {daemon_addr} v', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_connect_timeout', 'The time limit for connecting to a Milter (mail filter) application, and for negotiating protocol options.',  '30s', 'timeunits', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_content_timeout', 'The time limit for sending message content to a Milter (mail filter) application, and for receiving the response.',  '5m', 'timeunits', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_data_macros', 'The macros that are sent to version 4 or higher Milter (mail filter) applications after the SMTP DATA command. See MILTER_README for a list of available macro names and their meanings.',  'i', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_default_action', 'The default action when a Milter (mail filter) application is unavailable or mis-configured.',  'tempfail', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_end_of_data_macros', 'The macros that are sent to Milter (mail filter) applications after the end of the message header. See MILTER_README for a list of available macro names and their meanings.',  'i', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_header_checks', 'Optional lookup tables for content inspection of message headers that are produced by Milter applications. See the header_checks(5) manual page available actions. Currently, PREPEND is not implemented.',  'mysql:/etc/postfix/mysql-milter_header_checks.cf', 'mysql-script', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_helo_macros', 'The macros that are sent to Milter (mail filter) applications after the SMTP HELO or EHLO command. See MILTER_README for a list of available macro names and their meanings.',  '{tls_version} {cipher} {cipher_bits} {cert_subject} {cert_issuer}', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_macro_daemon_name', 'The {daemon_name} macro value for Milter (mail filter) applications. See MILTER_README for a list of available macro names and their meanings.',  '$myhostname', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_macro_defaults', 'Optional list of name=value pairs that specify default values for arbitrary macros that Postfix may send to Milter applications. These defaults are used when there is no corresponding information from the message delivery context.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_macro_v', 'The {v} macro value for Milter (mail filter) applications. See MILTER_README for a list of available macro names and their meanings.',  '$mail_name $mail_version', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_mail_macros', 'The macros that are sent to Milter (mail filter) applications after the SMTP MAIL FROM command. See MILTER_README for a list of available macro names and their meanings.',  'i {auth_type} {auth_authen} {auth_author} {mail_addr} {mail_host} {mail_mailer}', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_protocol', 'The mail filter protocol version and optional protocol extensions for communication with a Milter application; prior to Postfix 2.6 the default protocol is 2. Postfix sends this version number during the initial protocol handshake. It should match the version number that is expected by the mail filter application (or by its Milter library).',  '6', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_rcpt_macros', 'The macros that are sent to Milter (mail filter) applications after the SMTP RCPT TO command. See MILTER_README for a list of available macro names and their meanings.',  'i {rcpt_addr} {rcpt_host} {rcpt_mailer}', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'milter_unknown_command_macros', 'The macros that are sent to version 3 or higher Milter (mail filter) applications after an unknown SMTP command. See MILTER_README for a list of available macro names and their meanings.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mime_boundary_length_limit', 'The maximal length of MIME multipart boundary strings. The MIME processor is unable to distinguish between boundary strings that do not differ in the first $mime_boundary_length_limit characters.',  '2048', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mime_header_checks', 'Optional lookup tables for content inspection of MIME related message headers, as described in the header_checks(5) manual page.',  'mysql:/etc/postfix/mysql-mime_header_checks.cf', 'mysql-script', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mime_nesting_limit', 'The maximal recursion level that the MIME processor will handle. Postfix refuses mail that is nested deeper than the specified limit.',  '100', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'minimal_backoff_time', 'The minimal time between attempts to deliver a deferred message; prior to Postfix 2.4 the default value was 1000s.',  '5m', 'timeunits', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'multi_instance_directories', 'An optional list of non-default Postfix configuration directories; these directories belong to additional Postfix instances that share the Postfix executable files and documentation with the default Postfix instance, and that are started, stopped, etc., together with the default Postfix instance. Specify a list of pathnames separated by comma or whitespace.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'multi_instance_enable', 'Allow this Postfix instance to be started, stopped, etc., by a multi-instance manager. By default, new instances are created in a safe state that prevents them from being started inadvertently. This parameter is reserved for the multi-instance manager.',  'no', 'yesno', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'multi_instance_group', 'The optional instance group name of this Postfix instance. A group identifies closely-related Postfix instances that the multi-instance manager can start, stop, etc., as a unit. This parameter is reserved for the multi-instance manager.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'multi_instance_name', 'The optional instance name of this Postfix instance. This name becomes also the default value for the syslog_name parameter.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'multi_instance_wrapper', 'The pathname of a multi-instance manager command that the postfix(1) command invokes when the multi_instance_directories parameter value is non-empty. The pathname may be followed by initial command arguments separated by whitespace; shell metacharacters such as quotes are not supported in this context.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'multi_recipient_bounce_reject_code', 'The numerical Postfix SMTP server response code when a remote SMTP client request is blocked by the reject_multi_recipient_bounce restriction.',  '550', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mydestination', 'The list of domains that are delivered via the $local_transport mail delivery transport. By default this is the Postfix local(8) delivery agent which looks up all recipients in /etc/passwd and /etc/aliases. The SMTP server validates recipient addresses with $local_recipient_maps and rejects non-existent recipients. See also the local domain class in the ADDRESS_CLASS_README file',  '$myhostname, localhost.$mydomain, localhost', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mydomain', 'The internet domain name of this mail system. The default is to use $myhostname minus the first component, or "localdomain" (Postfix 2.3 and later). $mydomain is used as a default value for many other configuration parameters.',  '%domain', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'myhostname', 'The internet domain name of this mail system. The default is to use $myhostname minus the first component, or "localdomain" (Postfix 2.3 and later). $mydomain is used as a default value for many other configuration parameters.',  '%hostname', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mynetworks', 'The list of "trusted" remote SMTP clients that have more privileges than "strangers".',  '127.0.0.0/8 1.1.1.0/24 [::1]/128 [fe80::]/64', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'mynetworks_style', 'The method to generate the default value for the mynetworks parameter. This is the list of trusted networks for relay access control etc.',  'host', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'myorigin', 'The domain name that locally-posted mail appears to come from, and that locally posted mail is delivered to. The default, $myhostname, is adequate for small sites. If you run a domain with multiple machines, you should (1) change this to $mydomain and (2) set up a domain-wide alias database that aliases each user to user@that.users.mailhost.',  '$myhostname', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'nested_header_checks', 'Optional lookup tables for content inspection of non-MIME message headers in attached messages, as described in the header_checks(5) manual page.',  'mysql:/etc/postfix/mysql-nested_header_checks.cf', 'mysql-script', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'newaliases_path', 'Sendmail compatibility feature that specifies the location of the newaliases(1) command. This command can be used to rebuild the local(8) aliases(5) database.',  '/usr/bin/newaliases', 'path', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'non_fqdn_reject_code', 'The numerical Postfix SMTP server reply code when a client request is rejected by the reject_non_fqdn_helo_hostname, reject_non_fqdn_sender or reject_non_fqdn_recipient restriction.',  '504', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'non_smtpd_milters', 'A list of Milter (mail filter) applications for new mail that does not arrive via the Postfix smtpd(8) server. This includes local submission via the sendmail(1) command line, new mail that arrives via the Postfix qmqpd(8) server, and old mail that is re-injected into the queue with "postsuper -r". Specify space or comma as separator. See the MILTER_README document for details.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'notify_classes', 'The list of error classes that are reported to the postmaster. The default is to report only the most serious problems. The paranoid may wish to turn on the policy (UCE and mail relaying) and protocol error (broken mail software) reports.',  'resource, software', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'nullmx_reject_code', 'The numerical reply code when the Postfix SMTP server rejects a sender or recipient address because its domain has a nullmx DNS record (an MX record with an empty hostname). This is one of the possible replies from the restrictions reject_unknown_sender_domain and reject_unknown_recipient_domain.',  '556', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'openssl_path', 'The location of the OpenSSL command line program openssl(1). This is used by the "postfix tls" command to create private keys, certificate signing requests, self-signed certificates, and to compute public key digests for DANE TLSA records. In multi-instance environments, this parameter is always determined from the configuration of the default Postfix instance.',  'openssl', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'owner_request_special', 'Enable special treatment for owner-listname entries in the aliases(5) file, and don't split owner-listname and listname-request address localparts when the recipient_delimiter is set to "-". This feature is useful for mailing lists.',  'yes', 'yesno', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'parent_domain_matches_subdomains', 'A list of Postfix features where the pattern "example.com" also matches subdomains of example.com, instead of requiring an explicit ".example.com" pattern. This is planned backwards compatibility: eventually, all Postfix features are expected to require explicit ".example.com" style patterns when you really want to match subdomains.',  'debug_peer_list,fast_flush_domains,mynetworks,permit_mx_backup_networks,qmqpd_authorized_clients,relay_domains,smtpd_access_maps', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'permit_mx_backup_networks', 'Restrict the use of the permit_mx_backup SMTP access feature to only domains whose primary MX hosts match the listed networks. The parameter value syntax is the same as with the mynetworks parameter; note, however, that the default value is empty.',  '', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'pickup_service_name', 'The name of the pickup(8) service. This service picks up local mail submissions from the Postfix maildrop queue.',  'pickup', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'pipe_delivery_status_filter', 'Optional filter for the pipe(8) delivery agent to change the delivery status code or explanatory text of successful or unsuccessful deliveries. See default_delivery_status_filter for details.',  '$default_delivery_status_filter', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'plaintext_reject_code', 'The numerical Postfix SMTP server response code when a request is rejected by the reject_plaintext_session restriction.',  '450', 'integer', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'postlog_service_name', 'The name of the postlogd(8) service entry in master.cf. This service appends logfile records to the file specified with the maillog_file parameter.',  'postlog', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'postlogd_watchdog_timeout', 'How much time a postlogd(8) process may take to process a request before it is terminated by a built-in watchdog timer. This is a safety mechanism that prevents postlogd(8) from becoming non-responsive due to a bug in Postfix itself or in system software. This limit cannot be set under 10s.',  '10s', 'timeunits', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'postmulti_control_commands', 'The postfix(1) commands that the postmulti(1) instance manager treats as "control" commands, that operate on running instances. For these commands, disabled instances are skipped.',  'reload flush', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'postmulti_start_commands', 'The postfix(1) commands that the postmulti(1) instance manager treats as "start" commands. For these commands, disabled instances are "checked" rather than "started", and failure to "start" a member instance of an instance group will abort the start-up of later instances.',  'start', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'postmulti_stop_commands', 'The postfix(1) commands that the postmulti(1) instance manager treats as "stop" commands. For these commands, disabled instances are skipped, and enabled instances are processed in reverse order.',  'stop abort drain quick-stop', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'postscreen_access_list', 'Permanent white/blacklist for remote SMTP client IP addresses. postscreen(8) searches this list immediately after a remote SMTP client connects. Specify a comma- or whitespace-separated list of commands (in upper or lower case) or lookup tables. The search stops upon the first command that fires for the client IP address.',  'permit_mynetworks', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'postscreen_bare_newline_action', 'The action that postscreen(8) takes when a remote SMTP client sends a bare newline character, that is, a newline not preceded by carriage return.',  'ignore', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'postscreen_bare_newline_enable', 'Enable "bare newline" SMTP protocol tests in the postscreen(8) server. These tests are expensive: a remote SMTP client must disconnect after it passes the test, before it can talk to a real Postfix SMTP server.',  'no', 'yesno', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'postscreen_bare_newline_ttl', 'The amount of time that postscreen(8) will use the result from a successful "bare newline" SMTP protocol test. During this time, the client IP address is excluded from this test. The default is long because a remote SMTP client must disconnect after it passes the test, before it can talk to a real Postfix SMTP server.',  '30d', 'timeunits', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'postscreen_blacklist_action', 'The action that postscreen(8) takes when a remote SMTP client is permanently blacklisted with the postscreen_access_list parameter.',  'ignore', 'string', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'postscreen_cache_cleanup_interval', 'The amount of time between postscreen(8) cache cleanup runs. Cache cleanup increases the load on the cache database and should therefore not be run frequently. This feature requires that the cache database supports the "delete" and "sequence" operators. Specify a zero interval to disable cache cleanup.',  '12h', 'timeunits', '1', UNIX_TIMESTAMP());
INSERT INTO `config` (`form`, `name`, `description`, `values`, `typal`, `weight`, `created`) VALUES ('postfix-main.cf', 'postscreen_cache_map', 'Persistent storage for the postscreen(8) server decisions',  'btree:$data_directory/postscreen_cache', 'string', '1', UNIX_TIMESTAMP());
