function chronomail_get_compose_message(){if(window.tinyMCE&&(ed=tinyMCE.get(chronomail.env.composebody))){var a=ed.getContent();a=a.replace(/<blockquote[^>]*>(.|[\r\n])*<\/blockquote>/gmi,"")}else a=$("#"+chronomail.env.composebody).val(),a=a.replace(/^>.*$/gmi,"");return a}function chronomail_check_message(a){var b=chronomail.get_label("keywords","attachment_reminder").split(",").concat([".doc",".pdf"]);b=$.map(b,function(a){return RegExp.escape(a)});b=new RegExp("("+b.join("|")+")","i");return-1!=a.search(b)}
function chronomail_have_attachments(){return chronomail.env.attachments&&$("li",chronomail.gui_objects.attachmentlist).length}
function chronomail_attachment_reminder_dialog(){var a={};a[chronomail.get_label("addattachment")]=function(){$(this).remove();window.UI&&UI.show_uploadform?UI.show_uploadform():window.chronomail_ui&&chronomail_ui.show_popup&&chronomail_ui.show_popup("uploadmenu",!0)};a[chronomail.get_label("send")]=function(a){$(this).remove();chronomail.env.attachment_reminder=!0;chronomail.command("send","",a)};chronomail.env.attachment_reminder=!1;chronomail.show_popup_dialog(chronomail.get_label("attachment_reminder.forgotattachment"),chronomail.get_label("attachment_reminder.missingattachment"),
a,{button_classes:["mainaction attach","send"]})}window.chronomail&&chronomail.addEventListener("beforesend",function(a){a=chronomail_get_compose_message();var b=$("#compose-subject").val();if(!chronomail.env.attachment_reminder&&!chronomail_have_attachments()&&(chronomail_check_message(a)||chronomail_check_message(b)))return chronomail_attachment_reminder_dialog(),!1});
